server/storage.ts.content:
import { users, type User, type InsertUser } from "@shared/schema";

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  currentId: number;

  constructor() {
    this.users = new Map();
    this.currentId = 1;
  }

  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentId++;
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path, { dirname } from "path";
import { fileURLToPath } from "url";
import { createServer as createViteServer, createLogger, type ServerOptions } from "vite";
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions: ServerOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        __dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(__dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

client/src/App.tsx.content:
import { Canvas } from "@react-three/fiber";
import { Suspense, useState, useEffect } from "react";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import "@fontsource/inter";
import Scene from "./components/3d/Scene";
import UIOverlay from "./components/ui/UIOverlay";
import WebGLFallback from "./components/ui/WebGLFallback";
import { detectWebGL } from "./lib/utils/webgl";

const queryClient = new QueryClient();

function App() {
  const [mode, setMode] = useState<'checking' | '3d' | 'fallback'>('checking');
  const [canvasKey, setCanvasKey] = useState(0);
  const [error, setError] = useState<string | undefined>();

  useEffect(() => {
    // Check WebGL support on component mount
    const checkWebGL = () => {
      try {
        const support = detectWebGL();
        console.log('WebGL Support Check:', support);

        if (support.isSupported) {
          setMode('3d');
        } else {
          setMode('fallback');
          setError(support.error);
        }
      } catch (error) {
        console.error('WebGL detection error:', error);
        setMode('fallback');
        setError(error instanceof Error ? error.message : 'WebGL detection failed');
      }
    };

    // Immediate check - no delay needed
    checkWebGL();
  }, []);

  // Show loading state while checking WebGL
  if (mode === 'checking') {
    return (
      <div className="w-screen h-screen bg-black flex items-center justify-center">
        <div className="text-cyan-400 font-mono text-center">
          <div className="text-4xl mb-4">üåå WLSFX</div>
          <div className="text-lg animate-pulse">Initializing...</div>
        </div>
      </div>
    );
  }

  // Show fallback UI if WebGL is not supported or context was lost
  if (mode === 'fallback') {
    return (
      <QueryClientProvider client={queryClient}>
        <WebGLFallback error={error} />
      </QueryClientProvider>
    );
  }


  // Show 3D portfolio if WebGL is supported
  return (
    <QueryClientProvider client={queryClient}>
      <div style={{ width: '100vw', height: '100vh', position: 'relative', overflow: 'hidden', background: '#000' }}>
        <Canvas
          key={canvasKey}
          dpr={[1, 1]}
          gl={{ 
            antialias: false, 
            powerPreference: 'low-power', 
            failIfMajorPerformanceCaveat: false, 
            alpha: true, 
            stencil: false, 
            depth: true, 
            preserveDrawingBuffer: false 
          }}
          camera={{
            position: [0, 5, 15],
            fov: 60
          }}
          onCreated={({ gl }) => {
            console.log('WebGL renderer initialized successfully');
            const canvas = gl.domElement as HTMLCanvasElement;

            const onLost = (e: Event) => { 
              e.preventDefault(); 
              console.warn('WebGL context lost'); 
              setMode('fallback');
              setError('WebGL context was lost');
            };

            const onRestored = () => { 
              console.info('WebGL context restored'); 
              setCanvasKey(k => k + 1); 
              setMode('3d'); 
            };

            const onCreationError = (e: any) => { 
              console.error('WebGL creation error', e?.statusMessage); 
              setMode('fallback');
              setError('WebGL context creation failed');
            };

            canvas.addEventListener('webglcontextlost', onLost as EventListener, { passive: false });
            canvas.addEventListener('webglcontextrestored', onRestored as EventListener);
            canvas.addEventListener('webglcontextcreationerror', onCreationError as EventListener);
          }}
          onError={(error) => {
            console.error('Canvas error occurred:', error);
            // Don't immediately fallback on every error - many are recoverable
            // Only fallback on critical WebGL context errors
            if (error && (error.toString().includes('context') || error.toString().includes('WebGL'))) {
              console.error('Critical WebGL error detected, switching to fallback');
              setMode('fallback');
              setError('Canvas rendering failed: ' + error.toString());
            } else {
              console.warn('Non-critical Canvas error, continuing in 3D mode:', error);
            }
          }}
        >
          <color attach="background" args={["#000"]} />
          <Suspense fallback={null}>
            <Scene />
          </Suspense>
        </Canvas>
        <UIOverlay />
      </div>
    </QueryClientProvider>
  );
}

export default App;

