          {/* Skills Section */}
          {activeSection === 'skills' && (
            <div className="bg-gray-900/50 backdrop-blur-sm rounded-lg p-6 border border-pink-400/30">
              <h2 className="text-2xl text-pink-400 mb-6">Skills</h2>
