            </div>
          )}

          {/* Contact Section */}
          {activeSection === 'contact' && (
            <div className="bg-gray-900/50 backdrop-blur-sm rounded-lg p-6 border border-blue-400/30">
              <h2 className="text-2xl text-blue-400 mb-6">Contact</h2>
              <div className="text-center mb-6">
